// Recopilar datos de los nodos diarios y mensuales
const q1Data = $('1. Bot Leads (Ayer)').all().map(i => i.json);
const q1bData = $('1b. Bot Leads (Mes Actual)').all().map(i => i.json);
const q2Data = $('2. Modo (Ayer)').all().map(i => i.json);
const q2bData = $('2b. Modo (Mes Actual)').all().map(i => i.json);
const q3Data = $('3. Retail (Ayer)').all().map(i => i.json);
const q3bData = $('3b. Retail (Mes Actual)').all().map(i => i.json);
const q4Data = $('4. Inventario (Ayer)').all().map(i => i.json);
const q4bData = $('4b. Inventario (Mes Actual)').all().map(i => i.json);

// Estilos Compartidos
const headerStyle = 'background-color: #ecf0f1; border: 1px solid #bdc3c7; padding: 6px; color: #2c3e50; text-align: center; font-weight: bold; font-family: Arial, sans-serif; font-size: 12px;';
const cellStyle = 'border: 1px solid #bdc3c7; padding: 6px; text-align: center; font-family: Arial, sans-serif; font-size: 12px;';
const titleStyle = 'font-family: Arial, sans-serif; color: #2c3e50; border-bottom: 2px solid #3498db; padding-bottom: 5px; margin-top: 20px;';

// Helper de Color
const getBgColor = (val) => {
  val = parseInt(val) || 0;
  if (val < 0) return '#ffcccc'; // Rojo
  if (val > 0) return '#ccffcc'; // Verde
  return '#fff5cc'; // Amarillo
};

// --- Función Tabla 1: Resumen General ---
function buildGeneralTable(data, title) {
  let html = `<h3 style="${titleStyle}">${title}</h3>`;
  if (!data || data.length === 0 || (data.length === 1 && Object.keys(data[0]).length === 0)) return html + "<p style='font-family:Arial; font-size:12px'>No se encontraron datos para ayer.</p>";

  html += '<table style="width: 100%; border-collapse: collapse; margin-bottom: 10px;">';
  html += '<thead><tr>';
  // NUEVOS HEADERS (INCLUYE ERROR)
  const headers = ['Fecha', 'Total Leads', 'Inm. Ident.', '% Ident.', 'Dieron Horario', '% Horario', 'Error', '% Error', 'Leads Modo', 'Leads Retail'];
  headers.forEach(h => html += `<th style="${headerStyle}">${h}</th>`);
  html += '</tr></thead><tbody>';

  for (const row of data) {
    if (Object.keys(row).length === 0) continue;
    const dateStr = row.dia ? new Date(row.dia).toISOString().split('T')[0] : 'N/A';
    const pct = row.porc_identificado ? Math.round(row.porc_identificado * 100) + '%' : '0%';
    // NUEVAS COLUMNAS
    const pctHorario = row.porc_dio_horario ? Math.round(row.porc_dio_horario * 100) + '%' : '0%';
    const pctError = row.porc_con_error ? Math.round(row.porc_con_error * 100) + '%' : '0%';

    html += '<tr>';
    html += `<td style="${cellStyle}"><strong>${dateStr}</strong></td>`;
    html += `<td style="${cellStyle}">${row.cant_leads || 0}</td>`;
    html += `<td style="${cellStyle}">${row.cant_leads_inmueble_identificado || 0}</td>`;
    html += `<td style="${cellStyle}">${pct}</td>`;
    // NUEVOS VALORES
    html += `<td style="${cellStyle}">${row.cant_leads_dieron_horario || 0}</td>`;
    html += `<td style="${cellStyle}">${pctHorario}</td>`;
    html += `<td style="${cellStyle}">${row.cant_leads_con_error || 0}</td>`;
    html += `<td style="${cellStyle}">${pctError}</td>`;

    html += `<td style="${cellStyle}">${row.cant_leads_modo || 0}</td>`;
    html += `<td style="${cellStyle}">${row.cant_leads_retail || 0}</td>`;
    html += '</tr>';
  }
  html += '</tbody></table>';
  return html;
}

