  headers.forEach(h => html += `<th style="${headerStyle}">${h}</th>`);
  html += '</tr></thead><tbody>';

  for (const row of data) {
    if (Object.keys(row).length === 0) continue;
    const dateStr = row.mes ? new Date(row.mes).toISOString().split('T')[0].substring(0, 7) : 'N/A';
    const pct = row.porc_identificado ? Math.round(row.porc_identificado * 100) + '%' : '0%';
    const pctHorario = row.porc_dio_horario ? Math.round(row.porc_dio_horario * 100) + '%' : '0%';
