    html += `<td style="${cellStyle}">${pct}</td>`;
    html += `<td style="${cellStyle}">${row.cant_leads_dieron_horario || 0}</td>`;
    html += `<td style="${cellStyle}">${pctHorario}</td>`;
    html += `<td style="${cellStyle}">${row.cant_leads_con_error || 0}</td>`;
    html += `<td style="${cellStyle}">${pctError}</td>`;
    html += `<td style="${cellStyle}">${row.cant_leads_modo || 0}</td>`;
    html += `<td style="${cellStyle}">${row.cant_leads_retail || 0}</td>`;
    html += '</tr>';
  }
  html += '</tbody></table>';
  return html;
}

// --- Función Tabla 2 y 3: Operativa DIARIA (Modo/Retail) ---
function buildTargetTable(data, title) {
  let html = `<h3 style="${titleStyle}">${title}</h3>`;
  if (!data || data.length === 0 || (data.length === 1 && Object.keys(data[0]).length === 0)) return html + "<p style='font-family:Arial; font-size:12px'>No se encontraron datos.</p>";

  html += '<table style="width: 100%; border-collapse: collapse; margin-bottom: 10px;">';
  html += '<thead><tr>';
  const headers = ['Fecha', 'Leads Real', 'Target', 'Dif', 'Visitas Agend.', 'Target', 'Dif', '% Conv', 'Visitas Real.', 'Target', 'Dif', '% Conv'];
  headers.forEach(h => html += `<th style="${headerStyle}">${h}</th>`);
  html += '</tr></thead><tbody>';

  for (const row of data) {
    if (Object.keys(row).length === 0) continue;
    const dateStr = row.dia ? new Date(row.dia).toISOString().split('T')[0] : 'N/A';
    const colLead = getBgColor(row.diff_cant_leads_target);
    const colAgen = getBgColor(row.diff_cant_visitas_agendadas_target);
    const colReal = getBgColor(row.diff_cant_visitas_realizadas_target);
    const convAgen = row.conv_visitas_agendadas ? (row.conv_visitas_agendadas * 100).toFixed(0) + '%' : '0%';
    const convReal = row.conv_visitas_realizadas ? (row.conv_visitas_realizadas * 100).toFixed(0) + '%' : '0%';

    html += '<tr>';
    html += `<td style="${cellStyle}"><strong>${dateStr}</strong></td>`;
    html += `<td style="${cellStyle}">${row.cant_leads_real || 0}</td>`;
    html += `<td style="${cellStyle} color: #7f8c8d;">${row.cant_leads_target || 0}</td>`;
    html += `<td style="${cellStyle} background-color: ${colLead};">${row.diff_cant_leads_target || 0}</td>`;

    html += `<td style="${cellStyle}">${row.cant_visitas_agendadas_real || 0}</td>`;
    html += `<td style="${cellStyle} color: #7f8c8d;">${row.cant_visitas_agendadas_target || 0}</td>`;
    html += `<td style="${cellStyle} background-color: ${colAgen};">${row.diff_cant_visitas_agendadas_target || 0}</td>`;
    html += `<td style="${cellStyle}">${convAgen}</td>`;

    html += `<td style="${cellStyle}">${row.cant_visitas_realizadas || 0}</td>`;
    html += `<td style="${cellStyle} color: #7f8c8d;">${row.cant_visitas_realizadas_target || 0}</td>`;
    html += `<td style="${cellStyle} background-color: ${colReal};">${row.diff_cant_visitas_realizadas_target || 0}</td>`;
    html += `<td style="${cellStyle}">${convReal}</td>`;
    html += '</tr>';
  }
  html += '</tbody></table>';
  return html;
}

// --- Función Tabla MES (Modo/Retail) ---
// Incluye Nuevos Ingresos (Sin Entregas)
function buildMonthTable(data, title) {
  let html = `<h4 style="${titleStyle} border-bottom: 1px dashed #3498db; margin-top: 10px;">${title}</h4>`;
  if (!data || data.length === 0 || (data.length === 1 && Object.keys(data[0]).length === 0)) return html + "<p style='font-family:Arial; font-size:12px'>No se encontraron datos.</p>";

  html += '<table style="width: 100%; border-collapse: collapse; margin-bottom: 10px;">';
  html += '<thead><tr>';
  const headers = ['Mes', 'Leads', 'Visitas Agend.', 'Visitas Real.', 'Nuevos Ingresos', 'Tgt', 'Dif'];
  headers.forEach(h => html += `<th style="${headerStyle}">${h}</th>`);
  html += '</tr></thead><tbody>';

  for (const row of data) {
    if (Object.keys(row).length === 0) continue;
    const dateStr = row.mes ? new Date(row.mes).toISOString().split('T')[0].substring(0, 7) : 'N/A';
    const colIng = getBgColor(row.diff_nuevos_ingresos_target);

    html += '<tr>';
    html += `<td style="${cellStyle}"><strong>${dateStr}</strong></td>`;
    html += `<td style="${cellStyle}">${row.cant_leads_real || 0}</td>`;
    html += `<td style="${cellStyle}">${row.cant_visitas_agendadas_real || 0}</td>`;
    html += `<td style="${cellStyle}">${row.cant_visitas_realizadas || 0}</td>`;

    html += `<td style="${cellStyle}">${row.cant_nuevos_ingresos_real || 0}</td>`;
    html += `<td style="${cellStyle} color: #7f8c8d;">${row.nuevos_ingresos_target || 0}</td>`;
    html += `<td style="${cellStyle} background-color: ${colIng};">${row.diff_nuevos_ingresos_target || 0}</td>`;
    html += '</tr>';
  }
  html += '</tbody></table>';
  return html;
}

// --- Función Tabla 4 DIARIA: Inventario ---
function buildInventarioTable(data, title) {
  let html = `<h3 style="${titleStyle}">${title}</h3>`;
  if (!data || data.length === 0 || (data.length === 1 && Object.keys(data[0]).length === 0)) return html + "<p style='font-family:Arial; font-size:12px'>No se encontraron datos.</p>";

  html += '<table style="width: 100%; border-collapse: collapse; margin-bottom: 10px;">';
  html += '<thead><tr>';
  const headers = ['Fecha', 'Leads Real', 'Target', 'Dif', 'Leads en Cobertura', 'Target', 'Dif', 'Horarios', 'Llamadas', 'Target', 'Dif'];
  headers.forEach(h => html += `<th style="${headerStyle}">${h}</th>`);
  html += '</tr></thead><tbody>';

  for (const row of data) {
    if (Object.keys(row).length === 0) continue;
    const dateStr = row.dia ? new Date(row.dia).toISOString().split('T')[0] : 'N/A';
    const colLead = getBgColor(row.diferencia_leads_target);
    const colCob = getBgColor(row.diferencia_cobertura_target);
    const colLlam = getBgColor(row.dif_cant_llamadas_agendadas_target);

    html += '<tr>';
    html += `<td style="${cellStyle}"><strong>${dateStr}</strong></td>`;
    html += `<td style="${cellStyle}">${row.cant_leads_real || 0}</td>`;
    html += `<td style="${cellStyle} color: #7f8c8d;">${row.cant_leads_target || 0}</td>`;
    html += `<td style="${cellStyle} background-color: ${colLead};">${row.diferencia_leads_target || 0}</td>`;

    html += `<td style="${cellStyle}">${row.cant_leads_cobertura_real || 0}</td>`;
    html += `<td style="${cellStyle} color: #7f8c8d;">${row.cant_leads_cobertura_target || 0}</td>`;
    html += `<td style="${cellStyle} background-color: ${colCob};">${row.diferencia_cobertura_target || 0}</td>`;

    html += `<td style="${cellStyle}">${row.cant_leads_horarios || 0}</td>`;

    html += `<td style="${cellStyle}">${row.cant_llamadas_agendadas_real || 0}</td>`;
    html += `<td style="${cellStyle} color: #7f8c8d;">${row.cant_llamadas_agendadas_target || 0}</td>`;
    html += `<td style="${cellStyle} background-color: ${colLlam};">${row.dif_cant_llamadas_agendadas_target || 0}</td>`;
    html += '</tr>';
  }
  html += '</tbody></table>';
  return html;
}

// --- Función Tabla 4 MES: Inventario ---
function buildInventarioMonthTable(data, title) {
  let html = `<h4 style="${titleStyle} border-bottom: 1px dashed #3498db; margin-top: 10px;">${title}</h4>`;
  if (!data || data.length === 0 || (data.length === 1 && Object.keys(data[0]).length === 0)) return html + "<p style='font-family:Arial; font-size:12px'>No se encontraron datos.</p>";

  html += '<table style="width: 100%; border-collapse: collapse; margin-bottom: 10px;">';
  html += '<thead><tr>';
  const headers = ['Mes', 'Leads Tot', 'Tgt', 'Dif', 'Leads Cob', 'Tgt', 'Dif', '% Cob', 'Llamadas Agend', 'Inm. Recep', 'Tgt', 'Dif', 'Días Conv'];
  headers.forEach(h => html += `<th style="${headerStyle}">${h}</th>`);
  html += '</tr></thead><tbody>';

  for (const row of data) {
    if (Object.keys(row).length === 0) continue;
    const dateStr = row.mes ? new Date(row.mes).toISOString().split('T')[0].substring(0, 7) : 'N/A';
    const colLead = getBgColor(row.diff_leads);
    const colCob = getBgColor(row.diff_leads_cobertura);
    const colRecep = getBgColor(row.diff_recepciones_target);
    const pct = row.porcentaje_cobertura ? row.porcentaje_cobertura + '%' : '0%';

    html += '<tr>';
    html += `<td style="${cellStyle}"><strong>${dateStr}</strong></td>`;
    html += `<td style="${cellStyle}">${row.leads_totales || 0}</td>`;
    html += `<td style="${cellStyle} color: #7f8c8d;">${row.leads_target || 0}</td>`;
    html += `<td style="${cellStyle} background-color: ${colLead};">${row.diff_leads || 0}</td>`;

    html += `<td style="${cellStyle}">${row.cant_leads_cobertura_real || 0}</td>`;
    html += `<td style="${cellStyle} color: #7f8c8d;">${row.leads_cobertura_target || 0}</td>`;
    html += `<td style="${cellStyle} background-color: ${colCob};">${row.diff_leads_cobertura || 0}</td>`;

    html += `<td style="${cellStyle}">${pct}</td>`;
    html += `<td style="${cellStyle}">${row.cantidad_llamadas_agendadas || 0}</td>`;

    html += `<td style="${cellStyle}">${row.nuevos_inmuebles_recepcionados_propietario || 0}</td>`;
    html += `<td style="${cellStyle} color: #7f8c8d;">${row.recepciones_target || 0}</td>`;
    html += `<td style="${cellStyle} background-color: ${colRecep};">${row.diff_recepciones_target || 0}</td>`;

    html += `<td style="${cellStyle}">${row.dias_prom_conversion || 0}</td>`;
    html += '</tr>';
  }
  html += '</tbody></table>';
  return html;
}

let finalHtml = "";

finalHtml += buildGeneralTable(q1Data, "1. Resumen General Agente Corretaje (Ayer)");
finalHtml += buildGeneralMonthTable(q1bData, "Acumulado Agente Corretaje (Mes Actual)");

finalHtml += buildTargetTable(q2Data, "2. Detalle Operativo: MODO (Ayer)");
finalHtml += buildMonthTable(q2bData, "Detalle MODO acumulado mes actual");

finalHtml += buildTargetTable(q3Data, "3. Detalle Operativo: RETAIL (Ayer)");
finalHtml += buildMonthTable(q3bData, "Detalle RETAIL acumulado mes actual");

finalHtml += buildInventarioTable(q4Data, "4. Detalle Operativo: INVENTARIO (Ayer)");
finalHtml += buildInventarioMonthTable(q4bData, "Detalle INVENTARIO acumulado mes actual");

return [{ json: { html_final: finalHtml } }];
