const input = $('When Executed by Another Workflow').first().json;
const extracted = $input.first().json.output;

// Formatear teléfono
const telefonoLimpio = input.telefono ? input.telefono.replace(/[^0-9]/g, '').slice(-9) : '';
const telefonoFormateado = telefonoLimpio ? '+51' + telefonoLimpio : '';

return {
  json: {
    telefono: input.telefono,
    telefono_formateado: telefonoFormateado,
    nombre: input.nombre || '',
    ingresos: input.ingresos,
    inicial: input.inicial,
    ingresos_id: extracted.ingresos_id || 2379,
    inicial_id: extracted.inicial_id || 2391,
    ingresos_valor: extracted.ingresos_valor || 0,
    inicial_valor: extracted.inicial_valor || 0
  }
};

const contactIds = $input.first().json.result.CONTACT || [];
const datosPreparados = $('Preparar Datos').first().json;

return contactIds.map(id => ({
  contactId: id,
  ...datosPreparados
}));

const deals = $input.first().json.result || [];
const loopItem = $('Loop Contactos').item.json;

const dealsC5 = deals.filter(d => d.STAGE_ID && d.STAGE_ID.startsWith('C5'));

return {
  json: {
    ...loopItem,
    tieneDealsC5: dealsC5.length > 0,
    cod_asesor: dealsC5.length > 0 ? dealsC5[0].ASSIGNED_BY_ID : null,
    deal_id: dealsC5.length > 0 ? dealsC5[0].ID : null
  }
};

const asesor = $input.first().json || {};
const datos = $('Tiene Deal C5?').item.json;

return {
  json: {
    tiene_asesor: asesor.nombre ? true : false,
    asesor_nombre: asesor.nombre || '',
    asesor_telefono: asesor.telefono || '',
    asesor_correo: asesor.correo || '',
    asesor_codigo: datos.cod_asesor || '',
    contacto_id: datos.contactId,
    deal_id: datos.deal_id,
    cliente_nombre: datos.nombre,
    cliente_telefono: datos.telefono_formateado,
    es_contacto_nuevo: false,
    es_deal_nuevo: false
  }
};

const asesor = $input.first().json || {};
const deal = $('Obtener Deal').first().json.result;
const datos = $('Preparar Datos').first().json;

return {
  json: {
    tiene_asesor: asesor.nombre ? true : false,
    asesor_nombre: asesor.nombre || '',
    asesor_telefono: asesor.telefono || '',
    asesor_correo: asesor.correo || '',
    asesor_codigo: deal.ASSIGNED_BY_ID || '',
    contacto_id: deal.CONTACT_ID,
    deal_id: deal.ID,
    cliente_nombre: datos.nombre,
    cliente_telefono: datos.telefono_formateado,
    es_contacto_nuevo: false,
    es_deal_nuevo: true
  }
};

