const deal = $('HTTP Request').first().json.result;
const customFields = {};

for (const key in deal) {
  if (
    key.startsWith("UF_CRM_") &&
    deal[key] !== "" &&
    deal[key] !== null &&
    !(Array.isArray(deal[key]) && deal[key].length === 0)
  ) {
    customFields[key] = deal[key];
  }
}

// Retornar el JSON que luego insertarás en tu base de datos
return [
  {
    json: {
      custom_fields_json: customFields
    }
  }
];
