={{
  (()=>{
    const lead = $('Split Out1').item.json;
    const customFields = lead.custom_fields_values || [];

    // 1. Primero, buscamos si 'LinkUrbania' tiene algún valor.
    const cfUrbania = customFields.find(x => x.field_name === 'LinkUrbania');
    const hasLinkUrbania = (cfUrbania && cfUrbania.values && cfUrbania.values[0] && cfUrbania.values[0].value);

    if (hasLinkUrbania) {
      return 'URBANIA';
    }

    // 2. Si no, buscamos el valor de 'n8n - Origen' como alternativa.
    const cfOrigen = customFields.find(x => x.field_name === 'n8n - Origen');
    const origenValue = (cfOrigen && cfOrigen.values && cfOrigen.values[0]) ? cfOrigen.values[0].value : '';

    return origenValue;
  })()
}}

={{
  (() => {
    const statuses = $('HTTP Request').item.json?._embedded?.statuses ?? [];
    const statusId = $('Split Out1').item.json.status_id;
    const hit = statuses.find(s => String(s.id) === String(statusId));
    return hit?.name || '';
  })()
}}

={{
  (() => {
    const d = new Date($('Edit Fields').item.json.fechaCreacion * 1000);
    const day = d.getDay(); // domingo=0, lunes=1, ..., sábado=6
    const diff = (day === 0 ? -6 : 1) - day; // retroceder hasta lunes
    d.setDate(d.getDate() + diff);
    d.setHours(0, 0, 0, 0);
    return d.toISOString().split('T')[0]; // YYYY-MM-DD
  })()
}}

parameters.query:
SELECT EXISTS (
  SELECT 1
  FROM RENTAS_CRM.ENTREGA_INMUEBLE A
  JOIN RENTAS_CRM.INQUILINOS B ON A.INQUILINO_ID = B.ID
  WHERE RIGHT(REGEXP_REPLACE($1, '\D', '', 'g'), 9) IN (
    B.CLEAN_TELEFONO,
    RIGHT(REGEXP_REPLACE(B.TELEFONO, '\D', '', 'g'), 9)
  )
) AS ya_entregado;

