// ==========================================================================
// PROCESAMIENTO DE DATOS - REPORTE MATRICIAL V9 (INDICADOR + REFINAMIENTO)
// ==========================================================================

const leads = $input.all().map(i => i.json);

if (leads.length === 0) {
  return [{ json: { html_final: "<div style='font-family:Arial; padding:20px; text-align:center;'>No hay datos para procesar.</div>" } }];
}

// 1. CONFIGURACIÓN DE TIEMPO (GMT-5 PERÚ)
const now = new Date();
const peruOffset = -5;
const peruTime = new Date(now.getTime() + (peruOffset * 60 * 60 * 1000));
const yesterdayPeru = new Date(peruTime.getTime() - (24 * 60 * 60 * 1000));
const yesterdayStr = yesterdayPeru.getUTCFullYear() + '-' + 
                     String(yesterdayPeru.getUTCMonth() + 1).padStart(2, '0') + '-' + 
                     String(yesterdayPeru.getUTCDate()).padStart(2, '0');
const currentMonthStr = peruTime.getUTCFullYear() + '-' + 
                        String(peruTime.getUTCMonth() + 1).padStart(2, '0');

// 2. ESTILOS BASE
const tableStyle = 'width: 100%; border-collapse: collapse; margin-bottom: 5px; border: 1px solid black;';
const headerStyle = 'padding: 6px; text-align: center; font-weight: bold; font-family: Arial, sans-serif; font-size: 10px; border: 1px solid black;';
const cellStyle = 'border: 1px solid black; padding: 6px; text-align: center; font-family: Arial, sans-serif; font-size: 10px; min-width: 60px;';
const labelCellStyle = 'border: 1px solid black; padding: 6px; text-align: left; font-family: Arial, sans-serif; font-size: 10px; font-weight: bold; background-color: #f9f9f9; width: 140px;';
const titleStyle = 'font-family: Arial, sans-serif; font-weight: bold; font-size: 14px; margin-bottom: 5px; margin-top: 25px; color: #333;';
const subTitleStyle = 'font-family: Arial, sans-serif; font-size: 12px; margin-bottom: 8px; color: #666; font-weight: bold;';

// 3. LÓGICA DE ESTADÍSTICAS
function getStats(data) {
  const IDS_INV = [97504464, 97504468, 97504472, 99685031];
  const IDS_RENT = [98000340, 99685039];
  const IDS_PROP = [99685043, 99685047];
  const IDS_CLI = [99685051, 99685055];
  const IDS_NO_ID = [96388008, 97504460, 99685035];

  const isAsesor = l => Number(l.status_id) === 97504472;
  const isReunion = l => l.charla_elegida && !['NINGUNO', 'VACIO'].includes(l.charla_elegida);
  const isSimulacion = l => Number(l.status_id) === 97504468;
  const isPropDerivado = l => Number(l.status_id) === 99685047;
  const isInqDerivado = l => Number(l.status_id) === 99685039;

  const invLeads = data.filter(l => IDS_INV.includes(Number(l.status_id)) || isAsesor(l) || isReunion(l) || isSimulacion(l));
  const rentLeads = data.filter(l => IDS_RENT.includes(Number(l.status_id)) || IDS_PROP.includes(Number(l.status_id)) || isPropDerivado(l) || isInqDerivado(l));

  const total_inversion = invLeads.length;
  const total_rentas = rentLeads.length;

  const inv_asesor = invLeads.filter(isAsesor).length;
  const inv_reunion = invLeads.filter(isReunion).length;
  const inv_simulacion = invLeads.filter(isSimulacion).length;
  const total_deriv_inv = invLeads.filter(l => isAsesor(l) || isReunion(l) || isSimulacion(l)).length;

  const rent_prop = rentLeads.filter(isPropDerivado).length;
  const rent_corretaje = rentLeads.filter(isInqDerivado).length;
  const total_deriv_rent = rentLeads.filter(l => isPropDerivado(l) || isInqDerivado(l)).length;

  return {
    matrix: {
      total_chats: data.length,
      total_inversion,
      total_rentas,
      no_identificado: data.filter(l => IDS_NO_ID.includes(Number(l.status_id))).length,
      inv_cliente_actual: data.filter(l => IDS_CLI.includes(Number(l.status_id)) && (l.categoria_completa || '').includes('INV')).length,
      inv_lead_potencial: invLeads.filter(l => Number(l.status_id) !== 99685031).length,
      rentas_cliente_actual: data.filter(l => IDS_CLI.includes(Number(l.status_id)) && !(l.categoria_completa || '').includes('INV')).length,
      rentas_lead_propietario: data.filter(l => IDS_PROP.includes(Number(l.status_id)) || isPropDerivado(l)).length,
      rentas_lead_inquilino: data.filter(l => IDS_RENT.includes(Number(l.status_id)) || isInqDerivado(l)).length
    },
    deriv_inv: {
      labels: ['Total Derivados Inversión', 'Derivados Asesor', 'Derivados Reunión', 'Simulación de Crédito'],
      values: [total_deriv_inv, inv_asesor, inv_reunion, inv_simulacion]
    },
    deriv_rent: {
      labels: ['Total Derivados Rentas', 'Derivado Asesor Propietario', 'Derivados Inquilino a Corretaje'],
      values: [total_deriv_rent, rent_prop, rent_corretaje]
    }
  };
}

const dayRes = getStats(leads.filter(l => l.fecha_dia && l.fecha_dia.startsWith(yesterdayStr)));
const monthRes = getStats(leads.filter(l => l.fecha_dia && l.fecha_dia.startsWith(currentMonthStr)));

// 4. RENDERS
function renderMatrix(stats) {
  const cols = [
    { label: 'Total Chats', key: 'total_chats', color: '#f2f2f2', txt: '#333' },
    { label: 'Inversión', key: 'total_inversion', color: '#ff9900', txt: 'white' },
    { label: 'Rentas', key: 'total_rentas', color: '#28a745', txt: 'white' },
    { label: 'No identificado', key: 'no_identificado', color: '#dc3545', txt: 'white' },
    { label: 'Inversión Cliente Actual', key: 'inv_cliente_actual', color: '#ff9900', txt: 'white' },
    { label: 'Inversión Lead potencial', key: 'inv_lead_potencial', color: '#ff9900', txt: 'white' },
    { label: 'Rentas Cliente Actual', key: 'rentas_cliente_actual', color: '#28a745', txt: 'white' },
    { label: 'Rentas lead Propietario', key: 'rentas_lead_propietario', color: '#28a745', txt: 'white' },
    { label: 'Rentas Lead Inquilino', key: 'rentas_lead_inquilino', color: '#28a745', txt: 'white' }
  ];

  let html = `<table style="${tableStyle}"><thead><tr>`;
  html += `<th style="${headerStyle} background-color: #f2f2f2; border-color: black; min-width: 140px;">Indicador</th>`;
  cols.forEach(c => {
    html += `<th style="${headerStyle} background-color: ${c.color}; color: ${c.txt}; border-color: black;">${c.label}</th>`;
  });
  html += `</tr></thead><tbody><tr>`;
  html += `<td style="${labelCellStyle}">Nro</td>`;
  cols.forEach(c => {
    html += `<td style="${cellStyle}">${stats.matrix[c.key]}</td>`;
  });
  html += `</tr><tr>`;
  html += `<td style="${labelCellStyle}">%</td>`;
  cols.forEach(c => {
    const pct = stats.matrix.total_chats > 0 ? ((stats.matrix[c.key] / stats.matrix.total_chats) * 100).toFixed(0) + '%' : '0%';
    html += `<td style="${cellStyle}">${pct}</td>`;
  });
  html += `</tr></tbody></table>`;
  return html;
}

function renderDerivTable(deriv, totalBase, pctLabel, themeColor) {
  let html = `<table style="${tableStyle}"><thead><tr>`;
  html += `<th style="${headerStyle} background-color: #f2f2f2; border-color: black; min-width: 140px;">Indicador</th>`;
  deriv.labels.forEach((l, i) => {
    html += `<th style="${headerStyle} background-color: ${themeColor}; color: white; border-color: black;">${l}</th>`;
  });
  html += `</tr></thead><tbody><tr>`;
  html += `<td style="${labelCellStyle}">Nro</td>`;
  deriv.values.forEach(v => html += `<td style="${cellStyle}">${v}</td>`);
  html += `</tr><tr>`;
  html += `<td style="${labelCellStyle}">${pctLabel}</td>`;
  deriv.values.forEach(v => {
    const pct = totalBase > 0 ? ((v / totalBase) * 100).toFixed(0) + '%' : '0%';
    html += `<td style="${cellStyle}">${pct}</td>`;
  });
  html += `</tbody></table>`;
  return html;
}

function renderSection(stats, title, shortTitle) {
  let html = `<div style="${titleStyle}">${title}</div>`;
  html += renderMatrix(stats);

  html += `<div style="${subTitleStyle}; margin-top: 15px;">${shortTitle}</div>`;
  html += `<div style="display: flex; gap: 6px;">`;
  html += `<div style="flex: 1.3;">${renderDerivTable(stats.deriv_inv, stats.matrix.total_inversion, "% del total chats inversión", "#ff9900")}</div>`;
  html += `<div style="flex: 1;">${renderDerivTable(stats.deriv_rent, stats.matrix.total_rentas, "% del total chats rentas", "#28a745")}</div>`;
  html += `</div>`;
  return html;
}

let finalHtml = `<div style="padding:10px; max-width: 1250px; margin: auto;">`;
finalHtml += renderSection(dayRes, "REPORTE DIARIO", "Diario");
finalHtml += `<br><div style='border-top: 1px dashed #ccc; margin: 25px 0;'></div>`;
finalHtml += renderSection(monthRes, "REPORTE ACUMULADO DEL MES", "Acumulado");
finalHtml += `</div>`;

return [{ json: { html_final: finalHtml } }];
